mapper.select:
SELECT count(*)
FROM basket
 WHERE member_no = #{no}

  		SELECT
		    cqna_no, m.member_no, cq_title, course_no,
		    DATE_FORMAT(cq_writedate, '%Y-%m-%d') AS cq_writedate
		FROM member m JOIN cqna cq ON m.member_no = cq.member_no
		WHERE m.member_no = #{member_no}
		ORDER BY cq_writedate;

SELECT count(*)
FROM review
 WHERE course_no = #{no}
