mapper.insert:
INSERT INTO member(
	name, id, pwd, email, gender, birthday, hp, addr1, addr2, registdate, zipcode, user_validity, email_agree
) VALUES (
	#{name}, #{id}, MD5(#{pwd}), #{email}, #{gender}, #{birthday}, #{hp}, #{addr1}, #{addr2}, NOW(), #{zipcode}, 1, #{email_agree}
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE member SET
pwd = MD5(#{pwd}),
gender = #{gender}, birthday = #{birthday}, hp = #{hp}, zipcode = #{zipcode}, addr1 = #{addr1}, addr2 = #{addr2} WHERE member_no = #{member_no}

UPDATE member
SET user_validity = false
where member_no= #{member_no}

mapper.select:
SELECT COUNT(*)
FROM member
WHERE id = #{xxx}

