mapper.insert:
INSERT INTO member(
	name, id, pwd, email, gender, birthday, hp, addr1, addr2, registdate, zipcode, user_validity, email_agree
) VALUES (
	#{name}, #{id}, MD5(#{pwd}), #{email}, #{gender}, #{birthday}, #{hp}, #{addr1}, #{addr2}, NOW(), #{zipcode}, 1, #{email_agree}
)

	SELECT LAST_INSERT_ID()

