mapper.insert:
insert into review (
	member_no, course_no, detail_no, r_title, r_content, r_writedate, r_rating
) values (
	#{member_no}, #{course_no}, #{detail_no}, #{r_title}, #{r_content}, now(), #{r_rating}
)

mapper.select:
select course_rating
from course
where course_no = #{no}

mapper.update:
UPDATE course
SET course_rating = #{rating}
WHERE course_no = #{no};

